mapper.select:
select
store_cate_detail_pk as pk, store_cate_detail_code as code, store_cate_detail_name as name from (select store_category_detail_tb.*,row_number() over(order by store_cate_detail_pk desc) as num from store_category_detail_tb) where num between #{start} and #{end}
ow_store_name as owStoreName, ow_photo_route as owPhotoRoute, rownum from owner_store_info_tb, owner_store_photo_tb where ow_store_info_fk = ow_store_info_pk and rownum between #{start} and #{end} order by rownum
