mapper.select:
select distinct ow_store_info_pk as owStoreInfoPk, ow_store_name as owStoreName, substr(ow_store_addr,6,2) as owStoreAddrFirst, substr(ow_store_addr,instr(ow_store_addr,' ')+1,instr(ow_store_addr,'구')-instr(ow_store_addr,' ')) as owStoreAddrFinal, ow_store_comment as owStoreComment, ow_store_addr as owStoreAddr, (select store_cate_main_name from store_category_main_tb where store_cate_main_pk = st.ow_store_big_type_fk) as storeCateMainName, (select store_cate_detail_name from store_category_detail_tb where store_cate_detail_pk = st.ow_store_detail_type_fk) as storeCateDetailName, (select ow_photo_route||'\'||ow_remake_name from owner_store_photo_tb where ow_store_photo_pk = (select min(ow_store_photo_pk) from owner_store_photo_tb where ow_store_info_fk = ow_store_info_pk and ow_photo_type_fk = 3)) as owPhotoRoute from owner_store_info_tb st, owner_store_detail_tb dt, (select row_number() over(order by ow_store_info_pk desc) as num from owner_store_info_tb) where ow_store_index = ow_store_info_pk and num between #{start} and #{end}
and store_cate_main_name = #{food}
and ow_store_name like '%'||#{keyword}||'%' or ow_store_tip like '%'||#{keyword}||'%' or store_cate_detail_name like '%'||#{keyword}||'%'
and store_cate_detail_name in
#{detailName}
and ow_budget in
#{item}
and ow_sub_info in
#{owSubInfo}
and ow_drink_list_info in
#{owDrinkListInfo}
