mapper.select:
select mb_entire_pk as memberEntire, mb_id as memberId, mb_pw as memberPw, mb_name as memberName, mb_nickname as memberNickName, mb_gender as memberGender, mb_birth as memberBirth, mb_email as memberEmail, mb_phone as memberPhone, mb_regDate as memberRegDate, mb_avatar as memberAvatar
from member_entire_tb
where mb_id=#{memberId} and mb_pw=#{memberPw}
